#include <stdio.h>#include <iostream>using namespace std;int pattern[30];	//n段の時の階段の上り方のパターンshort n;			//階段の段数short year;			//必要年数int main(void){	//1〜3段めの定義	pattern[0] = 1;	pattern[1] = 2;	pattern[2] = 4;		//漸化式を用いてパターンを計算	for( int i = 3 ; i < 30; ++i ){				//漸化式を用いて四段目以降を計算		pattern[i] = pattern[i - 1] + pattern[i - 2] + pattern[i - 3];	}		/*****【test】1段〜30段まですべて出力*****/	//for(int i=0;i<30;++i)printf("%d\n",pattern[i]);		//メインループ	while(1){			//階段の段数を取得		scanf("%d",&n);				//nが0の時break		if(!n)break;				//n段の場合の年数を求める		year = pattern[n - 1] / 3650;		if(pattern[n - 1] % 3650)++year;				//年数を表示		cout << year << endl;	}}