#include <stdio.h>#include <iostream>using namespace std;long long n;		//要素数の変数//行列の構造体struct matrix{	int a,b,c,d;};//行列同士のかけ算matrix mult(matrix x,matrix y){	//計算用構造体	matrix z = {0};		//行列の乗算	z.a = x.a * y.a + x.b * y.c;	z.b = x.a * y.b + x.b * y.d;	z.c = x.c * y.a + x.d * y.c;	z.d = x.c * y.b + x.d * y.d;		//計算結果を返す	return z;}int main(){	//行列二種	matrix X,Y;	X.a = Y.a = 1;	X.b = Y.b = 1;	X.c = Y.c = 1;	X.d = Y.d = 0;		//メインループ	while(1)	{		//要素数を受け取る		cin >> n;				//終了判定		if(n == -1)break;				//フィボナッチ計算		for(int i = 1;i < n;++i){						//乗算計算			X = mult(X,Y);						//200回に1回で剰余を求める			if(i * 0.005){				X.a %= 10000;				X.b %= 10000;				X.c %= 10000;				X.d %= 10000;			}		}				//結果を表示		printf("%d\n",X.c % 10000);				//変数の初期化		X.a = Y.a = 1;	X.b = Y.b = 1;		X.c = Y.c = 1;	X.d = Y.d = 0;	}	}