#include <stdio.h>#include <iostream>#include <math.h>using namespace std;int dataset;double zero = 0.0000000001;struct xy{	double x;	double y;};xy a,b,c,d,ab,cd;int main(){	//データセット数を受け取る	cin >> dataset;		//受け取った回数分roop	for(int i = 0;i < dataset;++i){				//データを受け取る		cin >> a.x;		cin >> a.y;		cin >> b.x;		cin >> b.y;		cin >> c.x;		cin >> c.y;		cin >> d.x;		cin >> d.y;				//ABベクトルとCDベクトルを求める		ab.x = a.x - b.x;		ab.y = a.y - b.y;		cd.x = c.x - d.x;		cd.y = c.y - d.y;				//ABベクトルとCDベクトルの外積(の大きさ)を求める		double data = ab.x * cd.y - ab.y * cd.x;		data = fabs(data);				//外積が0になっていれば平行		if(data < zero)	puts("YES");		else			puts("NO");	}}