#include <stdio.h>#include <iostream>#include <math.h>using namespace std;const double zero = 0.0000000001;struct xy{	double x;	double y;};xy a,b,c,p,ab,ap,bc,bp,ca,cp;int main(){	//無限roop	while(1){				//データを受け取る		int check = scanf("%lf",&a.x);		if(check == EOF)break;		cin >> a.y;		cin >> b.x;		cin >> b.y;		cin >> c.x;		cin >> c.y;		cin >> p.x;		cin >> p.y;				//BAとPA,CBとPB,ACとPCの外戚をそれぞれ求める		ab.x = a.x - b.x;		ab.y = a.y - b.y;		ap.x = a.x - p.x;		ap.y = a.y - p.y;		double data1 = ab.x * ap.y - ab.y *ap.x; 				bc.x = b.x - c.x;		bc.y = b.y - c.y;		bp.x = b.x - p.x;		bp.y = b.y - p.y;		double data2 = bc.x * bp.y - bc.y * bp.x;				ca.x = c.x - a.x;		ca.y = c.y - a.y;		cp.x = c.x - p.x;		cp.y = c.y - p.y;		double data3 = ca.x * cp.y - ca.y * cp.x;				//外積から点の座標を算出		if(data1 > zero){			if(data2 > zero && data3 > zero)puts("YES");			else							puts("NO");		}else{			if(data2 < -zero && data3 < -zero)	puts("YES");			else								puts("NO");		}	}}