#include <iostream>#include <stdio.h>using namespace std;int cost[20][20];int main(){	//必要変数の宣言	int cities,roads;	int city1,city2,cost1,cost2;	int start,goal,get,pay;		//配列の初期化	for(int i = 0;i < 20;++i)for(int j = 0;j < 20;++j)cost[i][j] = 1001001001;		//町と道の数を読み込む	scanf("%d%d",&cities,&roads);		//移動コストデータを読み込む	for(int i = 0;i < roads;++i){				//データの受け取り		scanf("%d,%d,%d,%d",&city1,&city2,&cost1,&cost2);				//読み込み確認		//cerr << "読み込み結果：" << city1 << " " << city2 << " " << cost1 << " " << cost2 << endl;				//データの格納		cost[city1 - 1][city2 - 1] = cost1;		cost[city2 - 1][city1 - 1] = cost2;	}		//仕事に関するデータを読み込む	scanf("%d,%d,%d,%d",&start,&goal,&get,&pay);		//最短路を算出	for(int k = 0;k < cities;++k){		for(int i = 0;i < cities;++i){			for(int j = 0;j < cities;++j){				if(cost[i][j] > cost[i][k] + cost[k][j])cost[i][j] = cost[i][k] + cost[k][j];			}		}	}		//必要データのみ出力	cout << get - pay - cost[start-1][goal-1] - cost[goal-1][start-1] << endl;}