#include <stdio.h>#include <stdlib.h>#include <iostream>using namespace std;int num;				//ソート対象個数int numbers[1000000];	//ソート数入れ場int compare(const int* x,const int* y){return *x - *y;}int main(){		//個数を受け取る	cin >> num;		//ソート対象数を受け取る	for(int i = 0;i < num;++i)cin >> numbers[i];		//ソートする	qsort(numbers,num,sizeof(int), (int(*)(const void*,const void*))compare );		//出力する	for(int i = 0;i < num;++i){		if(i)cout << " ";		cout << numbers[i];	}		//改行	cout << endl;}