#include <iostream>using namespace std;int main(){	//割る数	int n = 2;		//数値を受けとる	int num;	cin >> num;		//最初の表示	cout << num << ":";		//2から順に割り切れるか確かめる	while(1){		//割る		if(num % n == 0){			//割り切れたら割って表示			num /= n;			cout << " " << n;		}else{			//割り切れなかったら数字を増やす			++n;		}				//可能性がなくなったら終了		if(n * n > num){			//最後の数字を表示			cout << " " << num << endl;						//終了			break;		}	}}