#include <iostream>using namespace std;#define AY			600#define ARRAY		360000#define ARRAY1		360001int main(){	//素数セット用配列	int pn[ARRAY1];		//要素番号と同じ数字を入れる	for(int i = 0;i <= ARRAY;++i)pn[i] = i;		//素数以外を全て0に書き換える	pn[1] = 0;	for(int i = 2;i <= AY;++i){		//i番の配列の中身が0なら次に進む		if(pn[i] == 0)continue;				//i以外のiの倍数の要素番号の配列を全て0に書き換える				//for(int j = i+1;j <= ARRAY;++j){		//	if(j % i == 0)pn[j] = 0;		//}				int delmax = ARRAY / i;		for(int j = 2;j <= delmax;++j)pn[i * j] = 0;	}		//解析ループ	for(;;){		//足す回数nを受け取る		int n;		cin >> n;				//nが0なら即終了		if(n == 0)break;				//必要変数		int plus = 0;		//素数を合計何回足したか		int sum = 0;	//素数合計値入れ場		int check = 0;		//確認する配列の要素番号				//n個の素数を足し終わるまでループをまわす		while(plus < n){			//要素が0出なければ足す			if(pn[check]){				//合計値を増やす				sum += pn[check];								//カウント回数を増やす				++plus;			}						//checkを増やす			++check;		}				//出力		cout << sum << endl;	}}