#include <stdio.h>#include <iostream>using namespace std;#define hfor(X)		for(int h = 0;h < (X);++h)#define ifor(X)		for(int i = 0;i < (X);++i)#define jfor(X)		for(int j = 0;j < (X);++j)int vertices;			//頂点数bool matrix[101][101];	//隣接行列int main(){	//頂点数を受け取る	cin >> vertices;		//超点数だけリストから行列への変換処理を行う	hfor(vertices){				//操作するエッジ番号を受け取る		int fromedge;		cin >> fromedge;				//つながるエッジの数を受け取る		int edges;		cin >> edges;				//edgeの数だけループしてつながる先を受け取る		ifor(edges){			//つながる先を受け取る			int toedge;			cin >> toedge;						//行列を操作			matrix[fromedge][toedge] = true;		}	}		//出力する	for(int i = 1;i <= vertices;++i)for(int j = 1;j <= vertices;++j){		cout << matrix[i][j];		if(j == vertices)	cout << endl;		else				cout << " ";	}}