#include <stdio.h>#include <math.h>#include <iostream>using namespace std;int n;						//読み込み個数double x[100],y[100];		//頂点座標double s;					//面積int main(){	//読み込み個数を受け取る	cin >> n;		//読み込みループ	for(int i = 0;i < n;++i){		//座標を受け取る		cin >> x[i];		cin >> y[i];	}		//面積計算	for(int i = 0;i+2 < n;++i){			//三角形の個数分ループをまわす		//座標の設定		double ax = x[0];		double ay = y[0];		double bx = x[i+1];		double by = y[i+1];		double cx = x[i+2];		double cy = y[i+2];				//ABベクトル		double abx = bx - ax;		double aby = by - ay;				//ACベクトル		double acx = cx - ax;		double acy = cy - ay;				//外戚から面積を計算して加算		s += (abx*acy - aby*acx);	}		//画面に出力	printf("%.1f\n",s * 0.5);		//終了	return 0;}