#include <stdio.h>#include <iostream>using namespace std;#define ifor(X)		for(int  i = 0; i < (X);++i )#define iifor(X)	for(int ii = 0;ii < (X);++ii)#define jfor(X)		for(int  j = 0; j < (X);++j )#define jjfor(X)	for(int jj = 0;jj < (X);++jj)int n,n1;				//項数,項数+1int a[101][101];		//項int sum[101][101];		//和int main(){	//項数を受け取る	cin >> n;	n1 = n+1;		//aを受け取る	ifor(n)jfor(n)cin >> a[i+1][j+1];	//i:y座標（行）　j:x座標（列）		//sumを設定する	ifor(n)jfor(n)sum[i+1][j+1] = sum[i][j+1] + sum[i+1][j] + a[i+1][j+1] - sum[i][j];		//最大値変数	int maxsum = -999999999;		//n1-i番目からj番目を引く形の調査	//sum[i][j] - sum[ii][j] - sum[i][jj] + sum[ii][jj]	ifor(n1)iifor(i)jfor(n1)jjfor(j){		if(maxsum < sum[i][j] - sum[ii][j] - sum[i][jj] + sum[ii][jj])maxsum = sum[i][j] - sum[ii][j] - sum[i][jj] + sum[ii][jj];	}		//最大値を出力	cout << maxsum << endl;		return 0;}