#include <stdio.h>#include <iostream>using namespace std;#define ifor(X)	for(int i = 0;i < (X);++i)int f(int x){return x*x;}int main(){	int d;		//横幅		//全体ループ	while(~scanf("%d",&d)){		//EOF==255より、リターン値のビット反転で受け取ったかどうかが判別できる		//必要変数群		int i = 1;		//カウンター		int s = 0;		//面積				//求積ループ		while(600 - i * d){			//面積加算			s += f(d * i) * d;						//カウント			++i;		}				//出力		printf("%d\n",s);	}}