#include <stdio.h>#include <iostream>using namespace std;bool nail[5001][5001];		//釘座標配列int nails;					//一辺の釘の本数int rubber;					//輪ゴムの数int answer;					//答えint main(){	//釘の本数と輪ゴムの数を受け取る	cin >> nails >> rubber;		//輪ゴムの数だけ使う釘を指定	for(int h=0;h<rubber;++h){		//問題文参照		int a,b,x;				//a,b,xの値を受け取る		cin >> a >> b >> x;				//中身を埋める		for( int i=0 ; i<=x ; ++i )for( int ii=0 ; ii<=x ; ++ii )if(i>=ii){			//重複なしの場合釘数をカウント			if(!nail[a+i][b+ii]){				nail[a+i][b+ii] = true;				++answer;			}		}	}		//答えを出力	cout << answer << endl;}