#include <stdio.h>#include <iostream>using namespace std;int mx,sum;int main(){	//メインループ	while(1)	{		//組み合わせ数		int count = 0;				//データ読み込み		cin >> mx >> sum;				//終了条件		if(!mx && !sum)break;				//組み合わせ数計算		for(int i = 1;i <= mx-2;++i){				//iが一番小さい数			for(int j = i+1;j <= mx-1;++j){		//jが真ん中の数				//i+jがsumを超えていたらやり直し				if(i+j>=sum)break;				for(int k = j+1;k <= mx;++k){		//kが一番大きい数					//条件を満たしたらカウントして次へ					if(i+j+k==sum){						++count;						break;					}				}			}		}				//答えを出力		cout << count << endl;	}}