#include <stdio.h>#include <iostream>using namespace std;#define NOFIND		10000#define ifor(X)		for(int i = 0;i < (X);++i)#define jfor(X)		for(int j = 0;j < (X);++j)short tarocard,hanacard;		//所持カード枚数short taro[100],hana[100];		//所持カードint main(){	//メインループ	while(1)	{		//太郎と花子の合計得点		int taropoi = 0;		int hanapoi = 0;				//交換カードの値		short taroex = -1, hanaex = -1, exsum = NOFIND;				//二人の所持カード枚数を受け取る		cin >> tarocard >> hanacard;				//終了条件		if(!tarocard && !hanacard)break;				//具体的なカードを受け取り点数を計算		ifor(tarocard){cin >> taro[i]; taropoi += taro[i];}		ifor(hanacard){cin >> hana[i]; hanapoi += hana[i];}				//交換処理		ifor(tarocard){			jfor(hanacard){				//交換成立確認				if(taropoi-taro[i]+hana[j]==hanapoi+taro[i]-hana[j]){					//交換カードの和を確認					if(taro[i]+hana[j]>exsum)continue;										//交換点数を記録					taroex = taro[i];					hanaex = hana[j];					exsum = taroex + hanaex;				}			}		}				//データ出力		if(exsum == NOFIND)	cout << -1 << endl;		else				cout << taroex << " " << hanaex << endl;	}}